package lecture_3;

import java.util.*;

public class Typecast_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float f = (8.9f);
		System.out.println(f);
		double d = 8.9;
		Scanner sc = new Scanner(System.in);
		f = sc.nextFloat();
		d = sc.nextDouble();
            System.out.println(f +""+d);
	}

}
